mapper.update:
update faq 
set postreadcount = postreadcount + 1 
where postno = #{postno}

updaate faqattach
set downloadcount = downloadcount + 1 
where postno = #{postno}

update faq 
set posttitle = #{posttitle}, postcontent = #{postcontent}, postopened = #{postopened}
where postno = #{postno}

mapper.select:
select count(*)
from c_faq
where o_postno = #{o_postno}
