mapper.select:
select * from lostandfound
where lnftype = #{lnfType}
order by lnfNo desc

select * from lostandfound
where lnfNo = #{lnfNo}
order by lnfNo desc

select * from attachment
where lnfNo = #{lnfNo}
order by lnfNo desc

mapper.update:
	  update lostandfound set 
    lnfEnrollName = #{lnfEnrollName}, lnfName = #{lnfName}, lnfPcRoomName = #{lnfPcRoomName}
    where lnfNo = #{lnfNo}
